body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    background-color: #2c3e50;
    margin: 0;
    padding: 0;
    color: #ecf0f1;
}

header {
    background-color: #1a252f;
    padding: 1rem 0;
    position: sticky;
    top: 0;
    z-index: 1000;
}

.header-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

header h1 {
    font-size: 1.5rem;
    color: #ecf0f1;
}

.desktop-nav ul {
    list-style-type: none;
    display: flex;
}

.desktop-nav ul li {
    margin-left: 1rem;
}

