    font-size: 1rem;
    transition: color 0.3s ease;
}

.desktop-nav ul li a:hover {
    color: #3498db;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    display: flex;
    flex-direction: column;
    align-items: stretch;
    transition: all 0.5s ease-in-out;
}

.image-container {
    position: relative;
    width: 100%;
    margin-bottom: 20px;
    transition: all 0.5s ease-in-out;
    overflow: hidden;
}

.responsive-image {
    width: 100%;
    height: auto;
    max-height: 80vh;
    object-fit: cover;
    display: block;
    transition: all 0.5s ease-in-out;
}

.text-description {
    display: block;
    margin-top: 20px;
    padding: 10px;
    background-color: rgba(240, 240, 240, 0.1);
    border-radius: 5px;
    transition: all 0.5s ease-in-out;
}

.spotify-container {
    width: 100%;
