document.addEventListener('DOMContentLoaded', function() {
    const infoIcon = document.querySelector('.info-icon');
    const mobileDescription = document.querySelector('.mobile-description');
    const imageContainer = document.querySelector('.image-container');
    const hamburgerMenu = document.querySelector('.hamburger-menu');
    const mobileNav = document.querySelector('.mobile-nav');
    const navLinks = document.querySelectorAll('nav a');

    infoIcon.addEventListener('click', function(event) {
        event.stopPropagation();
        mobileDescription.classList.toggle('show');
    });

    imageContainer.addEventListener('click', function() {
        if (window.innerWidth <= 767) {
            mobileDescription.classList.remove('show');
        }
    });

    hamburgerMenu.addEventListener('click', function() {
        this.classList.toggle('active');
        mobileNav.classList.toggle('active');
    });

    navLinks.forEach(link => {
        link.addEventListener('click', function(e) {
            e.preventDefault();
            const targetId = this.getAttribute('href').substring(1);
            const targetElement = document.getElementById(targetId);

            if (targetElement) {
                targetElement.scrollIntoView({
                    behavior: 'smooth'
                });
            }

            if (window.innerWidth <= 767) {
                mobileNav.classList.remove('active');
                hamburgerMenu.classList.remove('active');
            }
        });
    });

    let resizeTimer;
    window.addEventListener('resize', function() {
        clearTimeout(resizeTimer);
        resizeTimer = setTimeout(function() {
            if (window.innerWidth > 767) {
                mobileDescription.classList.remove('show');
                mobileNav.classList.remove('active');
                hamburgerMenu.classList.remove('active');
            }
        }, 250);
    });
});

document.addEventListener('DOMContentLoaded', () => {
    const commentButtons = document.querySelectorAll('.comment-button');
    const modal = document.getElementById('comment-modal');
    const closeButton = document.querySelector('.close-button');
    const modalIdeaTitle = document.getElementById('modal-idea-title');
    const commentsSection = document.getElementById('comments-section');
    const submitCommentButton = document.getElementById('submit-comment');
    const newCommentInput = document.getElementById('new-comment');

    let currentIdeaId = null;
    let currentIdeaTitle = '';

    // Sample comments storage (Replace with backend integration)
    const commentsData = {
        // Idea ID: [comments]
        1: [
            { user: 'Alice', comment: 'Great idea! Really aligns with our goals.' },
            { user: 'Bob', comment: 'How do you plan to implement this?' }
        ],
        // Add more ideas and their comments here
    };

    commentButtons.forEach(button => {
        button.addEventListener('click', () => {
            currentIdeaId = button.getAttribute('data-idea-id');
            const ideaLink = document.querySelector(`.idea-link[data-idea-id="${currentIdeaId}"]`);
            currentIdeaTitle = ideaLink.textContent;
            openModal(currentIdeaTitle, currentIdeaId);
        });
    });

    closeButton.addEventListener('click', closeModal);

    window.addEventListener('click', (event) => {
        if (event.target == modal) {
            closeModal();
        }
    });

    submitCommentButton.addEventListener('click', () => {
        const commentText = newCommentInput.value.trim();
        if (commentText === '') {
            alert('Please enter a comment.');
            return;
        }

        const userName = 'Anonymous'; // Replace with actual user data if available

        const newComment = { user: userName, comment: commentText };

        if (!commentsData[currentIdeaId]) {
            commentsData[currentIdeaId] = [];
        }
        commentsData[currentIdeaId].push(newComment);

        renderComments(currentIdeaId);
        newCommentInput.value = '';
    });

    function openModal(title, ideaId) {
        modalIdeaTitle.textContent = title;
        modal.style.display = 'block';
        renderComments(ideaId);
    }

    function closeModal() {
        modal.style.display = 'none';
        commentsSection.innerHTML = '';
        newCommentInput.value = '';
    }

    function renderComments(ideaId) {
        commentsSection.innerHTML = '';
        const comments = commentsData[ideaId] || [];
        if (comments.length === 0) {
            commentsSection.innerHTML = '<p>No comments yet. Be the first to comment!</p>';
            return;
        }
        comments.forEach(comment => {
            const commentDiv = document.createElement('div');
            commentDiv.classList.add('comment');
            commentDiv.innerHTML = `<strong>${comment.user}:</strong> ${comment.comment}`;
            commentsSection.appendChild(commentDiv);
        });
    }
});

/* Base styles */
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    background-color: #FFD580;
    color: #333;
}

header {
    background-color: #1a252f;
    position: sticky;
    top: 0;
    z-index: 1000;
}

.header-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 60px;
}

header h1 {
    font-size: 1.5rem;
    color: #ecf0f1;
}

.desktop-nav ul {
    list-style-type: none;
    display: flex;
}

.desktop-nav ul li {
    margin-left: 1rem;
}

.desktop-nav ul li a {
    color: #ecf0f1;
    text-decoration: none;
    font-size: 1rem;
    transition: color 0.3s ease;
}

.desktop-nav ul li a:hover {
    color: #3498db;
}

.hamburger-menu {
    display: none;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}

.image-container {
    flex: 1 1 100%;
    max-width: 100%;
    position: relative;
    margin-bottom: 20px;
}

.image-container img {
    width: 100%;
    height: auto;
    display: block;
}

.info-icon {
    position: absolute;
    bottom: 10px;
    right: 10px;
    width: 30px;
    height: 30px;
    background-color: rgba(0, 0, 0, 0.5);
    color: #fff;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
    font-style: italic;
}

.description {
    flex: 1 1 100%;
    margin-bottom: 20px;
}

.mobile-description {
    display: none;
}

.spotify-embed {
    flex: 1 1 100%;
}

/* Mobile styles */
@media screen and (max-width: 767px) {
    .header-content {
        height: 50px;
        padding: 0 15px;
        justify-content: space-between;
        align-items: center;
    }

    header h1 {
        font-size: 1.2rem;
        margin: 0;
    }

    header h1 .mobile-title {
        display: inline;
    }

    header h1 .desktop-title {
        display: none;
    }

    .desktop-nav {
        display: none;
    }

    .hamburger-menu {
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 24px;
        width: 30px;
        cursor: pointer;
    }

    .hamburger-menu span {
        display: block;
        width: 30px;
        height: 3px;
        background-color: #ecf0f1;
        transition: all 0.3s ease;
    }

    .mobile-nav {
        display: none;
        background-color: #1a252f;
        position: absolute;
        top: 50px;
        left: 0;
        right: 0;
        z-index: 999;
    }

    .mobile-nav.active {
        display: block;
    }

    .mobile-nav ul {
        list-style-type: none;
    }

    .mobile-nav ul li {
        padding: 15px 20px;
        border-bottom: 1px solid #2c3e50;
    }

    .mobile-nav ul li a {
        color: #ecf0f1;
        text-decoration: none;
        font-size: 1rem;
    }

    .description {
        display: none;
    }

    .mobile-description {
        display: none;
        background-color: rgba(0, 0, 0, 0.8);
        color: #fff;
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        padding: 20px;
        overflow-y: auto;
        z-index: 1000;
    }

    .mobile-description.show {
        display: block;
    }

    .container {
        flex-direction: column;
    }

    .image-container {
        flex: 1 1 100%;
        margin-bottom: 20px;
    }

    .spotify-embed {
        flex: 1 1 100%;
    }

    .spotify-embed iframe {
        height: 380px;
    }
}

