    margin: 0;
    padding: 0;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    background-color: #f4f4f4;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: center;
}

.image-container {
    flex: 1 1 100%;
    margin-bottom: 20px;
}

.responsive-image {
    width: 100%;
    height: auto;
    max-height: 80vh;
    object-fit: cover;
}

.spotify-container {
    flex: 1 1 100%;
    background-color: rgba(0, 0, 255, 0.5);
    padding: 10px;
}

.spotify-container iframe {
    border-radius: 12px;
    width: 100%;
    height: 380px;
}

@media screen and (min-width: 768px) {
    .image-container {
        flex: 1 1 48%;
        margin-bottom: 0;
    }

    .spotify-container {
        flex: 1 1 48%;
    }
}

@media screen and (max-width: 767px) {
    .container {
        padding: 0;
        flex-direction: column;
    }

    .image-container {
        margin-bottom: 0;
    }

    .spotify-container {
        margin-top: 0;
        padding: 0;
    }

    .spotify-container iframe {
        height: 80px;
        border-radius: 0;
    }
}

@media screen and (min-width: 768px) and (max-width: 1024px) {
    .container {
        flex-direction: column;
    }

    .image-container, .spotify-container {
        flex: 1 1 100%;
    }

    .spotify-container {
        margin-top: 20px;
    }
}

