    flex-direction: column;
    align-items: stretch;
    transition: all 0.3s ease-in-out;
}

.image-container {
    position: relative;
    width: 100%;
    margin-bottom: 20px;
    transition: all 0.3s ease-in-out;
}

.responsive-image {
    width: 100%;
    height: auto;
    max-height: 80vh;
    object-fit: cover;
    display: block;
    transition: all 0.3s ease-in-out;
}

.text-description {
    display: block;
    margin-top: 20px;
    padding: 10px;
    background-color: #f0f0f0;
    border-radius: 5px;
    transition: all 0.3s ease-in-out;
}

.spotify-container {
    width: 100%;
    padding: 20px;
    transition: all 0.3s ease-in-out;
}

