* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    background-color: #2c3e50;
    margin: 0;
    padding: 0;
    color: #ecf0f1;
}

header {
    background-color: #1a252f;
    padding: 1rem 0;
    position: sticky;
    top: 0;
    z-index: 1000;
}

.header-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

header h1 {
    font-size: 1.5rem;
    color: #ecf0f1;
}

.desktop-nav ul {
    list-style-type: none;
    display: flex;
}

.desktop-nav ul li {
    margin-left: 1rem;
}

.desktop-nav ul li a {
    color: #ecf0f1;
    text-decoration: none;
    font-size: 1rem;
    transition: color 0.3s ease;
}

.desktop-nav ul li a:hover {
    color: #3498db;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    display: flex;
    flex-direction: column;
    align-items: stretch;
    transition: all 0.5s ease-in-out;
}

.image-container {
    position: relative;
    width: 100%;
    margin-bottom: 20px;
    transition: all 0.5s ease-in-out;
    overflow: hidden;
}

.responsive-image {
    width: 100%;
    height: auto;
    max-height: 80vh;
    object-fit: cover;
    display: block;
    transition: all 0.5s ease-in-out;
}

.text-description {
    display: block;
    margin-top: 20px;
    padding: 10px;
    background-color: rgba(240, 240, 240, 0.1);
    border-radius: 5px;
    transition: all 0.5s ease-in-out;
}

.spotify-container {
    width: 100%;
    padding: 20px;
    transition: all 0.5s ease-in-out;
}

.spotify-container iframe {
    border-radius: 12px;
    width: 100%;
    height: 380px;
    transition: all 0.5s ease-in-out;
}

.info-icon {
    display: none;
    position: absolute;
    bottom: 10px;
    left: 10px;
    color: white;
    font-size: 24px;
    cursor: pointer;
    transition: all 0.3s ease-in-out;
    z-index: 1;
}

.mobile-description {
    display: none;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    min-height: 25%;
    max-height: 75%;
    background-color: rgba(0, 0, 0, 0.7);
    color: white;
    padding: 15px;
    overflow-y: auto;
    transition: all 0.3s ease-in-out;
    transform: translateY(100%);
    font-size: 14px;
    line-height: 1.4;
    z-index: 1;
}

.mobile-description.show {
    display: block;
    transform: translateY(0);
}

iframe {
    margin: 0;
    padding: 0;
    display: block;
}

.image-container, .spotify-container {
    font-size: 0;
    line-height: 0;
}

.hamburger-menu {
    display: none;
    cursor: pointer;
}

.hamburger-menu span {
    display: block;
    width: 25px;
    height: 3px;
    background-color: #ecf0f1;
    margin: 5px 0;
    transition: all 0.3s ease;
}

.mobile-nav {
    display: none;
    background-color: #1a252f;
    position: fixed;
    top: 60px;
    left: 0;
    right: 0;
    z-index: 999;
}

.mobile-nav ul {
    list-style-type: none;
}

.mobile-nav ul li {
    padding: 15px 20px;
    border-bottom: 1px solid #2c3e50;
}

.mobile-nav ul li a {
    color: #ecf0f1;
    text-decoration: none;
    font-size: 1rem;
}

@media screen and (min-width: 768px) {
    .container {
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: space-between;
    }

    .image-container {
        flex: 0 0 60%;
        transition: flex 0.5s ease-in-out;
    }

    .text-description {
        flex: 0 0 60%;
        order: 2;
        transition: flex 0.5s ease-in-out, order 0.5s ease-in-out;
    }

    .spotify-container {
        flex: 0 0 35%;
        transition: flex 0.5s ease-in-out;
    }

    .info-icon {
        display: none;
    }

    .desktop-nav {
        display: block;
    }

    .hamburger-menu {
        display: none;
    }

    header h1 .mobile-title {
        display: inline;
    }

    header h1 span:not(.mobile-title) {
        display: inline;
    }
}

@media screen and (max-width: 767px) {
    .header-content {
        flex-direction: row;
        justify-content: space-between;
        align-items: center;
    }

    header h1 {
        font-size: 1.2rem;
    }

    header h1 .mobile-title {
        display: inline;
    }

    header h1 span:not(.mobile-title) {
        display: none;
    }

    .desktop-nav {
        display: none;
    }

    .hamburger-menu {
        display: block;
    }

    .container {
        padding: 0;
        display: flex;
        flex-direction: column;
    }

    .image-container {
        margin: 0;
        padding: 0;
        transition: margin 0.5s ease-in-out, padding 0.5s ease-in-out;
    }

    .responsive-image {
        display: block;
        width: 100%;
        height: auto;
        transition: height 0.5s ease-in-out;
    }

    .text-description {
        display: none;
        transition: display 0.5s ease-in-out;
    }

    .info-icon {
        display: block;
        transition: display 0.5s ease-in-out;
    }

    .spotify-container {
        margin: 0;
        padding: 0;
        transition: margin 0.5s ease-in-out, padding 0.5s ease-in-out;
    }

    .spotify-container iframe {
        display: block;
        width: 100%;
        height: 80px;
        border: none;
        transition: height 0.5s ease-in-out;
    }

    .mobile-nav {
        display: none;
    }

    .mobile-nav.active {
        display: block;
    }
}

