    list-style-type: none;
    display: flex;
}

.desktop-nav ul li {
    margin-left: 1rem;
}

.desktop-nav ul li a {
    color: #ecf0f1;
    text-decoration: none;
    font-size: 1rem;
    transition: color 0.3s ease;
}

